'use strict';

/**
 *
 * @param {number} min
 * @param {number} max
 * @returns {*}
 */
module.exports = function randomIntegerInRange(min, max) {
  if (typeof min !== 'number' || typeof max !== 'number') {
    return null;
  }
  if (min === max) {
    return min;
  }
  return Math.floor(Math.random() * (max - min + 1)) + min;
};
