'use strict';

var assert = require('chai').assert;
var expect = require('chai').expect;
var randomIntegerInRange = require('./index');

describe('Testing the generation of a random number in the range of 1 - 10 inclusive', function() {
  it('should return an integer', function() {
    for (var i = 0; i < 100; i++) {
      var rand = randomIntegerInRange(1, 10);
      assert.isAbove(rand, 0, 'Integer is not bigger then 0');
      assert.isBelow(rand, 11, 'Integer is not smaller then 10');
    }
  });

  it('must provide the right types, else return null', function() {
    expect(randomIntegerInRange('', 10)).to.equal(null);
    expect(randomIntegerInRange('', '')).to.equal(null);
    expect(randomIntegerInRange()).to.equal(null);
    expect(randomIntegerInRange(null, null)).to.equal(null);
    expect(randomIntegerInRange(12, null)).to.equal(null);
  });
  it('if min and max are the same returns the value', function() {
    expect(randomIntegerInRange(10, 10)).to.equal(10);
  });
});
